import React, { Component } from 'react';
import { withAuth } from '../../lib/authContext'
import ResultUser from './ResultUser'
import ResultPost from './ResultPost'
import search from '../../lib/search-service'
import '../../stylesheets/search.css'

class SearchPage extends Component {

  state = {
    searchValue: '',
    selectedOption: 'user',
    searchResult: [],
  }

  // Selecciona quÃ© tipo con los radio button y lo guarda en el estado
  handleOptionChange = (e) => {
    const selectedOption = e.target.value
    this.setState({
      selectedOption,
      searchResult: []
    });
  }

  // Toma el texto de la bÃºsqueda y lo guarda en el estado
  handleInput = (e) => {
    const searchValue = e.target.value
    this.setState({
      searchValue,
    })
  }

  // Coge el tipo de los radios y la bÃºsqueda y hace una llamada axios
  handleSubmit = (e) => {
    e.preventDefault()

    const { searchValue, selectedOption } = this.state
      search.searchUser(selectedOption, searchValue)
      .then(searchResult => {
        this.setState({
          searchResult,
        })
      })
      .catch(error => console.log(error))
  }

  // Muestra un componente u otro segÃºn el tipo marcado en los radios
  setType = () => {
    const {searchResult, selectedOption} = this.state
    if(selectedOption === 'user'){
      return <ResultUser 
      data={searchResult}
    />
    }else if (selectedOption === 'title' || selectedOption === 'text'){
      return <ResultPost 
      data={searchResult}
    />
    }else{
      return <div></div>
    }
  }

  render() {
    const { searchValue, selectedOption } = this.state
    return ( 
      <div className='search-page'>
        <form onSubmit={this.handleSubmit}>
          <label>Searching for {searchValue}</label>
          <div className='search-option-container'>
            <label className='search-radio-buttons'>
              <input type="radio" value="user" checked={selectedOption === 'user'} onChange={this.handleOptionChange} />
              Users
            </label>
            <label className='search-radio-buttons'>
              <input type="radio" value="title" checked={selectedOption === 'title'} onChange={this.handleOptionChange} />
              Posts title
            </label>
            <label className='search-radio-buttons'>
              <input type="radio" value="text" checked={selectedOption === 'text'} onChange={this.handleOptionChange} />
              Post content
            </label>
          </div>
          <input type="text" onChange={this.handleInput} />
          <input type="submit" value="search" />
        </form>
        {this.setType()}
      </div>
    )
  }
}

export default withAuth(SearchPage);
