import React, { Component } from 'react';
import auth from '../../lib/auth-service';
import { withAuth } from '../../lib/authContext';
import '../../stylesheets/login-page.css'

class Login extends Component {
  state = {
    username: "",
    password: "",
  }

  handleFormSubmit = (event) => {
    event.preventDefault();
    const { username, password } = this.state

    auth.login({ username, password })
    .then( (user) => {
      this.props.setUser(user)
      this.props.history.push('/'); 
    })
    .catch( error => console.log(error) )
  }

  handleChange = (event) => {  
    const {name, value} = event.target;
    this.setState({[name]: value});
  }

  render() {
    const { username, password } = this.state;
    return (
      <div className="login-page">
        <div className="login-container">
          <h1 className="title-app">Step<span>S</span>ay</h1>
          <form onSubmit={this.handleFormSubmit} className="signup-login-elements">
            <input type="text" name="username" value={username} onChange={this.handleChange} placeholder="User name"/>
            <input type="password" name="password" value={password} onChange={this.handleChange} placeholder="Password"/>
            <input className="button" type="submit" value="Login" />
          </form>
        </div>
      </div>
    )
  }
}

export default withAuth(Login);
